import React, { Component } from 'react';
import './App.css';
import { Navbar, Nav, NavDropdown, Form, Button, FormControl } from 'react-bootstrap';
import { TimelineLite } from 'gsap';
import logo from './kart.png';
import start from './start.png';
import up from './up.png';
import down from './down.png';
import left from './left.png';
import right from './right.png';
import {level1} from './level1.jsx';



export default class AppDragDropDemo extends Component {

    animate = () => { var animation = new TimelineLite()
        let start = false;
        this.choix.tasks.forEach ((t) => {
            if(t.dirXy=="start"){start=true}
            if(start==true) {
                if (t.name == "Left") {
                    animation
                        .to(this.box, 2, {x: t.pas})
                }else if (t.name == "Bottom") {
                    animation
                        .to(this.box, 3, {y: t.pas})
                }else if (t.name == "Right") {
                    animation
                        .to(this.box, 3, {x: t.pas})
                }
            }
        });

        /* animation
           .to(this.box, 1, { x: -115 })
           .to(this.box, 1, { x: -230 })
           .to(this.box, 3, {rotation:-90, scale:1})
           .to(this.box, 1, { y: 110 })
          .to(this.box, 1, { y: 220 })
          .to(this.box, 1, { y: 320 })
          .to(this.box, 1, { y: 430 })
          .to(this.box, 1, { x: -120 })
          .to(this.box, 1, { x: 0 })
          .to(this.box, 1, { x: 120 })
          .to(this.box, 1, { x: 220 })
          .to(this.box, 1, { x: 320 })*/
    }

    state = {
        tasks: [
            {dirXy:"start",pas:"0",name:"Start",category:"wip", bgcolor: `url(${start})`},
            {dirXy:"x",pas:"320",name:"Right", category:"wip", bgcolor: `url(${right})`},
            {dirXy:"x",pas:"-230",name:"Left", category:"wip", bgcolor:`url(${left})`},
            {dirXy:"x",pas:"430",name:"Top", category:"wip", bgcolor: `url(${up})`},
            {dirXy:"x",pas:"430",name:"Bottom", category:"wip", bgcolor:`url(${down})`}
        ]
    }

    choix = {
        tasks: [

        ]
    }


    onDragStart = (ev, id) => {
        console.log('dragstart:',id);
        ev.dataTransfer.setData("id", id);
    }

    onDragOver = (ev) => {
        ev.preventDefault();
    }

    onDrop = (ev, cat) => {
        let id = ev.dataTransfer.getData("id");

        let tasks = this.state.tasks.filter((task) => {
            if (task.name == id) {
                if(task.category=="complete"){this.choix.tasks.pop(task);}
                if(task.category=="wip"){this.choix.tasks.push(task);}
                task.category = cat;
            }
            return task;
        });

        this.setState({
            ...this.state,
            tasks
        });
    }

    render() {


        var tasks = {
            wip: [],
            complete: []
        }
        let i = 0;
        this.state.tasks.forEach ((t) => {
            tasks["wip"].push(
                <div key={i++}
                     onDragStart = {(e) => this.onDragStart(e, t.name)}
                     draggable
                     className="draggable"
                     style = {{backgroundImage: t.bgcolor}}
                >
                </div>
            );
        });
        this.choix.tasks.forEach ((t) => {

            tasks["complete"].push(
                <div key={i++}
                     onDragStart = {(e) => this.onDragStart(e, t.name)}
                     draggable
                     className="draggable"
                     style = {{backgroundImage: t.bgcolor}}
                >
                </div>
            );
        });

        return (
            <div className="container-drag">
                <div className="wip"
                     onDragOver={(e)=>this.onDragOver(e)}
                     onDrop={(e)=>{this.onDrop(e, "wip")}}>
                    <span className="task-header">Deplacements</span>
                    {tasks.wip}
                </div>
                <div>
                    <div className="droppable"
                         onDragOver={(e)=>this.onDragOver(e)}
                         onDrop={(e)=>this.onDrop(e, "complete")}>
                        <span className="task-header">Choix</span>
                        {tasks.complete}
                    </div>
                    <nav class="navbar navbar-default">
                        <div class="container-fluid">
                            <div class="navbar-header">
                                <a class="navbar-brand" href="#">KidsCode</a>
                            </div>
                            <ul class="nav navbar-nav">
                                <li class="active"><a href="#">Home</a></li>
                                <li><a href="#">Statistiques</a></li>
                                <li><a href="#">Profil</a></li>
                            </ul>
                        </div>
                    </nav>
                    {/* Level : level1.jsx */}
                    <level1 />
                </div>



            </div>
        );
    }
}

const styles = {
    button: {
        width: 200,
        height: 45,
        border: 'none',
        outline: 'none',
    },
    container: {
        padding: 100,
    },
    box: {
    }
}
